import React from "react";
import axios from "axios";

export default function Comment({ singlePost }) {
  console.log(singlePost);
  return (
    <ul>
      {singlePost.map((comm) => (
        <li key={comm.id}>
          <h3>{comm.email}</h3>
          <p> {comm.name}</p>
          <p> {comm.body}</p>
        </li>
      ))}
    </ul>
  );
}

Comment.getInitialProps = async () => {
  const comment = await axios.get(
    "https://jsonplaceholder.typicode.com/comments?postId=3"
  );
  const { data } = comment;
  return { singlePost: data };
};
